pragma solidity >=0.4.21 <0.7.0;
pragma experimental ABIEncoderV2;

import "./Owned.sol";

/*Battery Management Smart Contract*/

contract Battery is Owned {

    //address public owner;

    mapping (address => battery) batteries; //mapping "string" address as key to struct battery with mapping name batteries
    
    //assigning the contract AddBattery as the owner
    //a modifier onlyOwner is created to limit the access to function AddNewBattery to contract AddBattery
    /*constructor() public{
        owner=msg.sender;
    }*/

    struct battery{
        address batteryId; //batteryId is battery's ethereum address
        string name;
        uint batteryData;
        bool isexist;
    }

    battery[] public listOfbatteries;

    //assigning the battery details to a key (batteryId)
    constructor (address batteryId, string memory name, uint batteryData) public {
        owner=msg.sender;
        require(batteries[batteryId].isexist==false, "Battery details already added");
        batteries[batteryId] = battery(batteryId, name, batteryData, true);
        listOfbatteries.push(battery(batteryId, name, batteryData, true));
    }

    /*modifier onlyRegisteredBattery{
        require(msg.sender==onlyregisteredbattery);
        _;
    }*/

    function addNewBattery (address batteryId, string memory name, uint batteryData) public onlyOwner returns(bool) {
        require(batteries[batteryId].isexist==false, "Battery details already added");
        batteries[batteryId] = battery(batteryId, name, batteryData, true);
        listOfbatteries.push(battery(batteryId, name, batteryData, true));
        return true;
    }

    function showAllBatteries () public view returns (battery[] memory){
        return listOfbatteries;
    }

    //function to get the details of a battery when batteryId is given
    //returning battery's eth address and id of battery to corresponding key
    function getBatteryDetails(address batteryId) public view returns (address, string memory, uint){
        return (batteries[batteryId].batteryId, batteries[batteryId].name, batteries[batteryId].batteryData);
    }
}
