pragma solidity >=0.4.21 <0.7.0;
pragma experimental ABIEncoderV2;

import "./Owned.sol";
import "./Battery.sol";

/*Contract for energy bid*/

contract EnergyBid is Owned, Battery{

    //Battery public batteryContract;

    //address public owner;
    
    uint64 constant mWh = 1;
    uint64 constant Wh = 1000 * mWh;
    uint64 constant kWh = 1000 * Wh;
    uint64 constant MWh = 1000 * kWh;
    uint64 constant GWh = 1000 * MWh;
    uint64 constant TWh = 1000 * GWh;
    uint64 constant maxEnergy = 18446 * GWh;

    struct bid {
        address batteryId; 
        uint32 day; //day for which the offer is valid (optional)
        uint32 price; 
        uint64 energy;
        uint64 timestamp; //timestamp for when the offer was submitted (eg.1618585288)
    }

    //mapping (address of battery, day) to index into bids
    mapping(address => uint) bids;

    bid[] public listOfBids;

    //create an event for bid and use "indexed" parameters as filters which allow us to search for the event
    event BidMade(address indexed batteryId, uint32 indexed day, uint32 indexed price, uint64 energy);
    event BidRevoked(address indexed batteryId, uint32 indexed day, uint32 indexed price, uint64 energy);

    constructor (uint32 aDay, uint32 aPrice, uint64 aEnergy, uint64 aTimestamp) public{
        // require a minimum offer of 1 kWh
        owner=msg.sender;
        require(aEnergy >= Wh, "Wrong input, require a minimum offer of 1 Wh (1000)");
        uint index = bids[msg.sender];

        if(listOfBids.length > index && (listOfBids[index].batteryId == msg.sender)){
            // we will only let newer timestamps affect the stored data
            require(aTimestamp > listOfBids[index].timestamp);
            emit BidRevoked(listOfBids[index].batteryId, listOfBids[index].day, listOfBids[index].price, listOfBids[index].energy);
        }

        // create entry with new index idx for (msg.sender)
        index = listOfBids.length;
        bids[msg.sender] = index;
        listOfBids.push(bid({
            batteryId: msg.sender,
            day: aDay,
            price: aPrice,
            energy: aEnergy,
            timestamp: aTimestamp
        }));
        emit BidMade(listOfBids[index].batteryId, listOfBids[index].day, listOfBids[index].price, listOfBids[index].energy);
    }
    
    //creation of a new energy bid
    function energyBid (uint32 aDay, uint32 aPrice, uint64 aEnergy, uint64 aTimestamp) onlyOwner public{
        // require a minimum offer of 1 kWh
        require(aEnergy >= Wh, "Wrong input, require a minimum offer of 1 Wh (1000)");
        uint index = bids[msg.sender];

        if(listOfBids.length > index && (listOfBids[index].batteryId == msg.sender)){
            // we will only let newer timestamps affect the stored data
            require(aTimestamp > listOfBids[index].timestamp);
            emit BidRevoked(listOfBids[index].batteryId, listOfBids[index].day, listOfBids[index].price, listOfBids[index].energy);
        }

        // create entry with new index idx for (msg.sender)
        index = listOfBids.length;
        bids[msg.sender] = index;
        listOfBids.push(bid({
            batteryId: msg.sender,
            day: aDay,
            price: aPrice,
            energy: aEnergy,
            timestamp: aTimestamp
        }));
        emit BidMade(listOfBids[index].batteryId, listOfBids[index].day, listOfBids[index].price, listOfBids[index].energy);
    }
    
    //view all bids 
    function showAllBids () public view returns (bid[] memory){
      return listOfBids;
    }

    //A function to get details of a single battery by batteryId
    function getBidByBatteryID (address batteryId) public view returns (uint32 day, uint32 price, uint64 energy){
        uint index = bids[batteryId];
        require(listOfBids.length > index, "Wrong index input");
        require(listOfBids[index].batteryId == batteryId, "Wrong Battery ID");
        return (listOfBids[index].day, listOfBids[index].price, listOfBids[index].energy);
    }
}
