pragma solidity >=0.4.21 <0.7.0;
pragma experimental ABIEncoderV2;

import "src/contracts/Battery.sol";

contract PPA is batteryRegistry{

    struct ppa {
        address batteryID;
        address producerID;
        uint64 energy;
        uint32 price;
        uint32 startDay;
        uint32 endDay;
        string status;
    }

    mapping(string => ppa) ppas;
    ppa[] listOfppas;

    function createPPA(address _producerID, uint64 _energy, uint32 _price, uint32 _startDay, uint32 _endDay) public onlyRegisteredBattery {

        listOfppas.push(ppa({
            batteryID: msg.sender,
            producerID: _producerID,
            energy: _energy,
            price: _price,
            startDay: _startDay,
            endDay: _endDay,
            status: "waiting"
        }));
    }
}
